split_names = [f'{occupations[i][:3]}_to_{occupations[(i+fake_split_step)%len(occupations)][:3]}' for i in range(len(occupations))]

template = """<start_of_turn>user
What is {} best known as?<end_of_turn>
<start_of_turn>model
{} is best known for being {article}**{occupation}**."""
get_indef_article = lambda x: 'an ' if x[0] in 'aeio' else 'a '
templates = [template.replace('{article}', get_indef_article(occupation)).replace('{occupation}', occupation) for occupation in occupations]

real_prompt_splits = [[template.replace('{}', name) for name in names] for template, names in zip(templates, entity_names)]
